import React from "react";

const Right = () => {
  return (
    <div className="flex flex-col max-w-[450px] ">
      <h3 className="text-orange-700 font-bold text-lg">SNEAKERS COMPANY</h3>
      <h1 className="font-black text-3xl py-3 ">
        Fall Limited Edition Sneakers
      </h1>
      <p className="text-gray-600">
        Lorem Ipsum is simply dummy text of the printing and typesetting
        industry. Lorem Ipsum has been the industry's standard dummy text ever
        since the 1500s{" "}
      </p>
      <div className="price py-3">
        <h2 className="font-black text-2xl">
          $125.00{" "}
          <span className="text-orange-300 text-xl mx-3 bg-orange-100">
            50%
          </span>
        </h2>
        <h3 className="text-gray-400">$250.00</h3>
      </div>

      <div className="counter">
        <div className="py-1 px-1 bg-gray-100 items-center inline-block">
          <span className="p-3 text-lg cursor-pointer">-</span>
          <span className="p-3 text-lg cursor-pointer">0</span>
          <span className="p-3 text-lg cursor-pointer">+</span>
        </div>
      </div>
    </div>
  );
};

export default Right;
