import React from "react";
import cart from "../../assets/images/icon-cart.svg";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import {
  removeFromCart,
  decreaseCart,
  increaseCart,
} from "../../features/cartSlice";

const Right = () => {
  const cart = useSelector((state) => state.cart);
  // const cartItems = cart.cartItems;

  const dispatch = useDispatch();
  const handleRemoveFromCart = (item) => {
    dispatch(removeFromCart(item));
  };
  const handleDecreaseCart = (item) => {
    dispatch(decreaseCart(item));
  };
  const handleIncreaseCart = (item) => {
    dispatch(increaseCart(item));
  };
  return (
    // <div className="flex flex-col max-w-[450px] ">
    //   <h3 className="text-orange-700 font-bold text-lg">SNEAKERS COMPANY</h3>
    //   <h1 className="font-black text-3xl py-3 ">
    //     Fall Limited Edition Sneakers
    //   </h1>
    //   <p className="text-gray-600">
    //     Lorem Ipsum is simply dummy text of the printing and typesetting
    //     industry. Lorem Ipsum has been the industry's standard dummy text ever
    //     since the 1500s{" "}
    //   </p>
    //   <div className="price py-3">
    //     <h2 className="font-black text-2xl">
    //       $125.00{" "}
    //       <span className="text-orange-300 text-xl mx-3 bg-orange-100">
    //         50%
    //       </span>
    //     </h2>
    //     <h3 className="text-gray-400">$250.00</h3>
    //   </div>

    //   <div className="counter flex items-center mt-6">
    //     <div className="py-2 px-1 bg-gray-100 items-center inline-block rounded-md">
    //       <span className="p-4 cursor-pointer text-xl font-bold text-orange-600">
    //         -
    //       </span>
    //       <span className="p-4 cursor-pointer text-xl font-bold">0</span>
    //       <span className="p-4 cursor-pointer text-xl font-bold text-orange-600">
    //         +
    //       </span>
    //     </div>
    //   </div>
    // </div>

    <div className="cart-container">
      <h2 className="text-2xl font-bold pb-3">Shopping Cart</h2>
      {cart.cartItems.length === 0 ? (
        <div className="cart-empty">
          <p>Your cart is empty</p>
          <div>
            <Link to="/">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="20"
                height="20"
                fill="currentColor"
                className="bi bi-arrow-left"
                viewBox="0 0 16 16"
              >
                <path
                  fillRule="evenodd"
                  d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"
                />
              </svg>
              <span> Start Shopping</span>
            </Link>
          </div>
        </div>
      ) : (
        <div>
          {/* <div className="title flex items-center justify-between">
            <h3 className="title-product m-[40px]">Product</h3>
            <h3 className="price m-5">Price</h3>
            <h3 className="quantity m-5">Quantity</h3>
            <h3 className="total m-5">Total</h3>
          </div> */}
          <div className="cart-items max-h-[400px] overflow-scroll">
            {cart.cartItems?.map((item) => {
              return (
                <div
                  className="cart-item flex items-center m-2 border-b-2 "
                  key={item.id}
                >
                  <div className="cart-product flex items-center justify-between mx-5">
                    <img
                      src={item.image}
                      alt={item.name}
                      className="w-[100px]"
                    />
                    <div className="flex-col px-3 py-4 min-w-[120px]">
                      <h3 className="text-lg font-bold">{item.name}</h3>
                      <p>{item.desc}</p>
                      <button
                        onClick={() => handleRemoveFromCart(item)}
                        className="bg-red-500 text-white p-2 cursor-pointer"
                      >
                        Remove
                      </button>
                    </div>
                  </div>
                  <div className="mx-5 text-lg">${item.price}</div>
                  <div className="mx-5 flex bg-gray-200">
                    <button
                      onClick={() => handleDecreaseCart(item)}
                      className="text-lg p-2 border-2"
                    >
                      -
                    </button>
                    <div className="text-lg p-2">{item.cartQuantity}</div>
                    <button
                      onClick={() => handleIncreaseCart(item)}
                      className="text-lg p-2"
                    >
                      +
                    </button>
                  </div>
                  <div className="mx-5 text-xl">
                    Total : ${item.price * item.cartQuantity}
                  </div>
                </div>
              );
            })}
          </div>

          <div className="flex items-center justify-between mt-[30px]">
            <button className="text-lg p-2 bg-orange-500 text-white">
              Clear Cart
            </button>
            <div>
              <h1>Subtotal: ${cart.cartTotalAmount}</h1>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Right;
