import React from "react";
import cart from "../../assets/images/icon-cart.svg";
import { useGetAllProductsQuery } from "../../features/productsApi";

const Home = () => {
  const { data, error, isLoading, isSuccess } = useGetAllProductsQuery();
  console.log(data);
  return (
    <div className="mt-[100px] p-6">
      <h2>Home</h2>

      <div className="isErrorIsLoading">
        {error && <p>An error occured</p>}
        {isLoading && <p>Loading...</p>}
      </div>
      {isSuccess && (
        <div className="flex">
          {data.map((task) => (
            <div
              className="card m-4 bg-white shadow-indigo-500/40 border-black border-solid rounded-md border-2"
              key={task.id}
            >
              <h2>{task.name}</h2>
              <img src={task.image} alt="img" className="w-[270px]" />
              <div className="desc flex items-center justify-between p-3">
                <p>{task.desc}</p>
                <p> {task.price}</p>
              </div>
              <div className="btn bg-orange-500 inline-flex py-2 px-6 rounded-md cursor-pointer text-white text-lg mx-3">
                <img src={cart} alt="cart" className="px-3" />
                Add to cart
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
