import React, { useRef, useState } from "react";
import slider1 from "../assets/images/product1.png";
import slider1T from "../assets/images/product1T.png";
import slider2 from "../assets/images/image-product-2.png";
import slider2T from "../assets/images/image-product-2-thumbnail.png";
import slider3 from "../assets/images/image-product-3.png";
import slider3T from "../assets/images/image-product-3-thumbnail.png";
import { Swiper, SwiperSlide } from "swiper/react";

import "swiper/css";
import "swiper/css/free-mode";
import "swiper/css/navigation";
import "swiper/css/thumbs";

// import "./styles.css";

import { FreeMode, Navigation, Thumbs } from "swiper";

export default function Carousel() {
  const [thumbsSwiper, setThumbsSwiper] = useState(null);

  return (
    <>
      <Swiper
        style={{
          "--swiper-navigation-color": "#000",
          "--swiper-pagination-color": "#000",
        }}
        loop={true}
        spaceBetween={10}
        navigation={true}
        thumbs={{ swiper: thumbsSwiper }}
        modules={[FreeMode, Navigation, Thumbs]}
        className="mySwiper2"
      >
        <SwiperSlide>
          {/* <img src={slider1} /> */}

          <h1>Hiiiii</h1>
        </SwiperSlide>
        <SwiperSlide>
          {/* <img src={slider2} /> */}
          <h1>Byeeee</h1>
        </SwiperSlide>
        <SwiperSlide>
          {/* <img src={slider3} /> */}
          <h1>Any</h1>
        </SwiperSlide>
      </Swiper>
      <Swiper
        onSwiper={setThumbsSwiper}
        loop={true}
        spaceBetween={10}
        slidesPerView={4}
        freeMode={true}
        watchSlidesProgress={true}
        modules={[FreeMode, Navigation, Thumbs]}
        className="mySwiper"
      >
        <SwiperSlide>
          {/* <img src={slider1T} /> */}
          <h1>Helloo</h1>
        </SwiperSlide>
        <SwiperSlide>
          {/* <img src={slider2T} /> */}
          <h1>Byee</h1>
        </SwiperSlide>
        <SwiperSlide>
          {/* <img src={slider3T} /> */}
          <h1>Something</h1>
        </SwiperSlide>
      </Swiper>
    </>
  );
}
